    public int solve(int[][] A) {
        int i = 0;
        int j = A[0].length - 1;
        int max = 0;
        int index = 0;
        while (i < A.length && j >= 0) {
            if (A[i][j] == 1) {
                int k = j;
                int curr_max = 0;
                while (k >= 0 && A[i][k] == 1) {
                    k--;
                    curr_max ++;
                }
                if (curr_max + A.length - j > max) {
                    index = i;
                }
                max = Math.max(max, curr_max + A.length - j);

                i++;
                j--;
            } else {
                i++;
            }
        }
        return index;
    }
