package org.example.dsa1.string;

public class CountTheTotalNumberOfAmazingSubString {
    public static void main(String[] args) {
        String A = "";
        System.out.println(new CountTheTotalNumberOfAmazingSubString().solve(A));
    }

    public int solve(String S) {
        int n = S.length();
        long count = 0;
        String vowels = "aeiouAEIOU";

        for (int i = 0; i < n; i++) {
            if (vowels.indexOf(S.charAt(i)) != -1) {
                count += (n - i);
            }
        }

        return (int)(count%10003);
    }
}
